import React from 'react';
import { Link } from '@reach/router';

import SiteNavbar from './SiteNavbar';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import LandingPhoto from '../img/landingPhoto.png';
import LandingDescriptionPhoto from '../img/landingDescriptionPhoto.png';

// import '../css/Landing.css';

const Landing = () => {
	const width = LandingPhoto.offsetWidth;
	console.log(width);
	return (
		<div>
			<SiteNavbar />
			<div style={style.LandingDiv}>
				<h3>Berkeley's Premiere Formula SAE Electric Vehicle Team</h3>
				<Button style={style.LandingButton}>
					<Link to="/about">
						<p>Learn More</p>
					</Link>
				</Button>
			</div>
			<Container fluid style={style.Body} className="static">
				<Row noGutters="true">
					<Col xs={6}>
						<h3 style={style.Header3}>Our Mission</h3>
						<hr style={style.Hr} />
						<div style={style.TextContainer}>
							<p style={style.Text}>
								Create a{' '}
								<strong style={style.Yellow}>
									community of diverse individuals
								</strong>{' '}
								that focuses on learning and skill development.
							</p>
							<p style={style.Text}>
								Research, design, manufacture, and test{' '}
								<strong style={style.Blue}>
									efficient, sustainable transportation technology
								</strong>{' '}
								at UC Berkeley.
							</p>
							<p style={style.Text}>
								Compete in the annual{' '}
								<strong style={style.Yellow}>
									Formula SAE Electric Competition
								</strong>{' '}
								to demonstrate our technology at a global scale.
							</p>
						</div>
						<Button style={style.JoinButton}>Join The Team</Button>
					</Col>
					<Col xs={6} className="text-center">
						<img
							src={LandingDescriptionPhoto}
							alt="landing description"
							style={style.TextImage}
						/>
					</Col>
				</Row>
			</Container>
		</div>
	);
};

const style = {
	LandingDiv: {
		width: '100%',
		height: '0',
		paddingTop: '36.137%',
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'flex-end',
		alignItems: 'center',
		backgroundImage: `url(${LandingPhoto})`,
		// backgroundPosition: '0 10vh',
		backgroundRepeat: 'no-repeat',
		// backgroundAttachment: 'fixed',
		backgroundSize: '100%',
	},
	LandingButton: {
		minWidth: '20%',
		background: '#000000',
		border: 'none',
		boxShadow: '0px 0px 8px #FFFFFF',
		fontSize: '22px',
		borderRadius: '9px',
		padding: '',
		margin: '7% 0 4% 0',
	},
	Body: {
		padding: '10vh 9%',
	},
	Header3: {
		fontSize: '46px',
		textTransform: 'uppercase',
		fontFamily: 'Titillium Web',
	},
	Hr: {
		borderBottom: '2px solid #FFFFFF',
		width: '70%',
		marginRight: '30%',
	},
	TextContainer: {
		padding: '3vh 10% 5vh 0',
	},
	Text: {
		fontSize: '22px',
		padding: '1.5vh 0',
		lineHeight: '1.3',
	},
	JoinButton: {
		minWidth: '50%',
		textTransform: 'uppercase',
		fontFamily: 'Titillium Web',
		background: '#FDB515',
		border: 'none',
		fontSize: '22px',
		padding: '.3rem 3rem',
	},
	TextImage: {
		maxWidth: '100%',
		maxHeight: '90%',
	},
	Blue: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#4592DB',
	},
	Yellow: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#FDB515',
	},
};

export default Landing;
