import React from 'react';

import SiteNavbar from './SiteNavbar';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import LandingPhoto from '../img/landingPhoto.png';
import LandingDescriptionPhoto from '../img/landingDescriptionPhoto.png';

// import '../css/Landing.css';

const Landing = () => {
	const width = LandingPhoto.offsetWidth;
	console.log(width);
	return (
		<div className="wrapper">
			<SiteNavbar />
			<div style={style.LandingDiv}>
				{/* <img src={LandingPhoto} alt="landing" /> */}
				{/* <h3>Berkeley's Premiere Formula SAE Electric Vehicle Team</h3>
				<Button style={style.LandingButton}>
					<p>Learn More</p>
				</Button> */}
			</div>
			<Container fluid style={{ padding: '2.5vh 10%' }} className="static">
				<Row>
					<Col xs={6}>
						<h3 style={style.Header3}>Our Mission</h3>
						<div style={style.TextContainer}>
							<p style={style.Text}>
								Create a{' '}
								<strong style={style.Yellow}>
									community of diverse individuals
								</strong>{' '}
								that focuses on learning and skill development.
							</p>
							<p style={style.Text}>
								Research, design, manufacture, and test{' '}
								<strong style={style.Blue}>
									efficient, sustainable transportation technology
								</strong>{' '}
								at UC Berkeley.
							</p>
							<p style={style.Text}>
								Compete in the annual{' '}
								<strong style={style.Yellow}>
									Formula SAE Electric Competition
								</strong>{' '}
								to demonstrate our technology at a global scale.
							</p>
						</div>
						<Button style={style.JoinButton}>Join The Team</Button>
					</Col>
					<Col xs={6} style={{ border: '1px solid black' }}>
						<img
							src={LandingDescriptionPhoto}
							alt="landing description"
							style={style.TextImage}
						/>
					</Col>
				</Row>
			</Container>
		</div>
	);
};

const style = {
	LandingDiv: {
		width: '100%',
		height: '60vh',
		// border: '3px solid white',
		// display: 'flex',
		// flexDirection: 'column',
		// justifyContent: 'flex-end',
		// alignItems: 'center',
		backgroundImage: `url(${LandingPhoto})`,
		backgroundPosition: '0 0',
		backgroundRepeat: 'no-repeat',
		backgroundAttachment: 'fixed',
		backgroundSize: '100%',
	},
	LandingButton: {
		minWidth: '20%',
		background: '#000000',
		border: 'none',
		boxShadow: '0px 0px 8px #FFFFFF',
		fontSize: '22px',
		padding: '',
	},
	Header3: {
		fontSize: '46px',
		textTransform: 'uppercase',
		fontFamily: 'Titillium Web',
	},
	TextContainer: {
		padding: '3vh 10% 5vh 0',
	},
	Text: {
		fontSize: '22px',
		padding: '2vh 0',
	},
	JoinButton: {
		width: '50%',
		textTransform: 'uppercase',
		fontFamily: 'Titillium Web',
	},
	TextImage: {
		width: '80%',
		margin: ' 0 0 0 13%',
	},
	Blue: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#4592DB',
	},
	Yellow: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#FDB515',
	},
};

export default Landing;
