import React from 'react';
import { Link } from '@reach/router';

import SiteNavbar from './SiteNavbar';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import LandingPhoto from '../img/landingPhoto.png';
import LandingLogo from '../img/landingLogo.png';
import LandingDescriptionPhoto from '../img/landingDescriptionPhoto.png';

import '../css/Landing.css';

const Landing = () => {
	return (
		<div>
			<SiteNavbar />
			<div style={style.LandingDiv}>
				<img src={LandingLogo} alt="landing logo" />
				<h3 style={style.LandingSubtitle}>
					Berkeley's Premiere Formula SAE Electric Vehicle Team
				</h3>
				<Link to="/about" className="landing-button">
					<Button className="landing-button">
						<p>Learn More</p>
					</Button>
				</Link>
			</div>
			<Container fluid className="body">
				<Row noGutters="true">
					<Col xs={6}>
						<h3>Our Mission</h3>
						<hr />
						<div style={style.TextContainer}>
							<p style={style.Text}>
								Create a{' '}
								<strong style={style.Yellow}>
									community of diverse individuals
								</strong>{' '}
								that focuses on learning and skill development.
							</p>
							<p style={style.Text}>
								Research, design, manufacture, and test{' '}
								<strong style={style.Blue}>
									efficient, sustainable transportation technology
								</strong>{' '}
								at UC Berkeley.
							</p>
							<p style={style.Text}>
								Compete in the annual{' '}
								<strong style={style.Yellow}>
									Formula SAE Electric Competition
								</strong>{' '}
								to demonstrate our technology at a global scale.
							</p>
						</div>
						<Button style={style.JoinButton}>
							<Link to="/apply">
								<p>Join The Team</p>
							</Link>
						</Button>
					</Col>
					<Col xs={6} className="text-center">
						<img
							src={LandingDescriptionPhoto}
							alt="landing description"
							style={style.TextImage}
						/>
					</Col>
				</Row>
			</Container>
		</div>
	);
};

const style = {
	LandingDiv: {
		width: '100%',
		//border: '1px solid blue',
		height: '0',
		paddingTop: '35%',
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
		justifyContent: 'flex-end',
		backgroundImage: `url(${LandingPhoto})`,
		// backgroundPosition: '0 10vh',
		backgroundRepeat: 'no-repeat',
		// backgroundAttachment: 'fixed',
		backgroundSize: '100%',
	},
	LandingSubtitle: {
		margin: '3% 0',
		color: '#ffffff',
	},
	TextContainer: {
		padding: '3vh 10% 5vh 0',
	},
	Text: {
		fontSize: '22px',
		padding: '1.5vh 0',
		lineHeight: '1.3',
	},
	JoinButton: {
		minWidth: '50%',
		textTransform: 'uppercase',
		fontFamily: 'Titillium Web',
		background: '#FDB515',
		border: 'none',
		fontSize: '23px',
		padding: '.3rem 3rem',
		borderRadius: '9px',
	},
	TextImage: {
		maxWidth: '100%',
		maxHeight: '90%',
	},
	Blue: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#4592DB',
	},
	Yellow: {
		textDecoration: 'none',
		fontWeight: '400',
		color: '#FDB515',
	},
};

export default Landing;
