import React from 'react';
import $ from 'jquery';

import SiteNavbar from './SiteNavbar';
import TeamMember from './TeamMember';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import HeaderTeam from '../img/HEADERmeettheteam.png';
import Member1 from '../img/1.jpg';
import Member2 from '../img/2.jpg';
import Member3 from '../img/3.jpg';
import Member4 from '../img/4.jpg';
import Member5 from '../img/5.jpg';
import Member6 from '../img/6.jpg';
import Member7 from '../img/7.jpg';

import '../css/Team.css';

const Team = () => {
	$(document).ready(function () {
		$('.fade-in-image').each(async function (i) {
			var top_half_of_object = $(this).offset().top + $(this).outerHeight() / 2;
			var bottom_of_window = $(window).scrollTop() + $(window).height();

			/* If the object is completely visible in the window, fade it it */
			if (bottom_of_window > top_half_of_object) {
				$(this).animate({ opacity: '1' }, 750);
			}
		});
		$('.fade-in').each(async function (i) {
			var top_half_of_object = $(this).offset().top + $(this).outerHeight() / 2;
			var bottom_of_window = $(window).scrollTop() + $(window).height();

			/* If the object is completely visible in the window, fade it it */
			if (bottom_of_window > top_half_of_object) {
				$(this).animate({ opacity: '1' }, 750);
			}
		});
		/* Every time the window is scrolled ... */
		$(window).scroll(async function () {
			/* Check the location of each desired element */
			$('.fade-in-image').each(function (i) {
				var top_half_of_object =
					$(this).offset().top + $(this).outerHeight() / 2;
				var bottom_of_window = $(window).scrollTop() + $(window).height();

				/* If the object is completely visible in the window, fade it it */
				if (bottom_of_window > top_half_of_object) {
					$(this).animate({ opacity: '1' }, 650);
				}
			});
			$('.fade-in').each(async function (i) {
				var top_half_of_object =
					$(this).offset().top + $(this).outerHeight() / 2;
				var bottom_of_window = $(window).scrollTop() + $(window).height();

				/* If the object is completely visible in the window, fade it it */
				if (bottom_of_window > top_half_of_object) {
					$(this).animate({ opacity: '1' }, 650);
				}
			});
		});
	});
	return (
		<div className="team">
			<SiteNavbar sticky="top" bg="#1c1c1c" />
			<img src={HeaderTeam} alt="header team" className="header" />
			<Container fluid className="member-container">
				<Row className="justify-content-around member-row fade-in">
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member4}
							name="Brad"
							role="President and Business & Operations"
							major="placeholder"
							bio="Hi! I'm Brad!"
						/>
					</Col>
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member7}
							name="Prakhar"
							role="Chief ME"
							major="placeholder"
							bio="Hi! I'm Prakhar!"
						/>
					</Col>
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member3}
							name="Suhas"
							role="Grad Advisor"
							major="placeholder"
							bio="Hi! I'm Suhas!"
						/>
					</Col>
				</Row>

				<Row className="justify-content-around member-row">
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member2}
							name="Mayank"
							role="Chassis"
							major="placeholder"
							bio="Hi! I'm Mayank!"
						/>
					</Col>
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member5}
							name="Zach"
							role="Dynamics"
							major="placeholder"
							bio="Hi! I'm Zach!"
						/>
					</Col>
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member6}
							name="Nimit"
							role="Powertrain"
							major="placeholder"
							bio="Hi! I'm Nimit!"
						/>
					</Col>
				</Row>

				<Row className="justify-content-around member-row">
					<Col xs={12} sm={4} className="text-center">
						<TeamMember
							image={Member1}
							name="Avinash"
							role="EECS"
							major="placeholder"
							bio="Hi! I'm Avinash!"
						/>
					</Col>
				</Row>
			</Container>
		</div>
	);
};

export default Team;
