import React from 'react';
import $ from 'jquery';

import '../css/Team.css';

const TeamMember = (props) => {
	$(document).ready(function () {
		$('.fade-in-image').each(async function (i) {
			var top_half_of_object = $(this).offset().top + $(this).outerHeight() / 2;
			var bottom_of_window = $(window).scrollTop() + $(window).height();

			/* If the object is completely visible in the window, fade it it */
			if (bottom_of_window > top_half_of_object) {
				$(this).animate({ opacity: '1' }, 750);
			}
		});
		$('.fade-in').each(async function (i) {
			var top_half_of_object = $(this).offset().top + $(this).outerHeight() / 2;
			var bottom_of_window = $(window).scrollTop() + $(window).height();

			/* If the object is completely visible in the window, fade it it */
			if (bottom_of_window > top_half_of_object) {
				$(this).animate({ opacity: '1' }, 750);
			}
		});
		/* Every time the window is scrolled ... */
		$(window).scroll(async function () {
			/* Check the location of each desired element */
			$('.fade-in-image').each(function (i) {
				var top_half_of_object =
					$(this).offset().top + $(this).outerHeight() / 2;
				var bottom_of_window = $(window).scrollTop() + $(window).height();

				/* If the object is completely visible in the window, fade it it */
				if (bottom_of_window > top_half_of_object) {
					$(this).animate({ opacity: '1' }, 650);
				}
			});
			$('.fade-in').each(async function (i) {
				var top_half_of_object =
					$(this).offset().top + $(this).outerHeight() / 2;
				var bottom_of_window = $(window).scrollTop() + $(window).height();

				/* If the object is completely visible in the window, fade it it */
				if (bottom_of_window > top_half_of_object) {
					$(this).animate({ opacity: '1' }, 650);
				}
			});
		});
	});
	return (
		<div>
			<div className="individual-container">
				<img
					src={props.image}
					alt={props.name}
					className="member-photo fade-in-image"
				/>
				<div className="overlay">
					<div className="overlay-text">{props.bio}</div>
				</div>
			</div>
			<h4 className="member-name fade-in">{props.name}</h4>
			<p className="member-role fade-in">{props.role}</p>
			<p className="member-major fade-in">{props.major}</p>
		</div>
	);
};

export default TeamMember;
