import React from 'react';
import { Link } from '@reach/router';

import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import LandingLogo from '../img/landingLogo.png';

const Landing = () => {
	return (
		<footer
			style={{
				padding: '2rem 0',
				background: '#2A2A2A',
			}}
			className="text-center"
		>
			<Container fluid>
				<Row className="justify-content-center align-items-center">
					<Col xs={4} className="text-left">
						<img src={LandingLogo} alt="landing logo" style={style.Logo} />
						<p style={style.DisclaimerText}>
							We are a student group acting independently of the University of
							California. We take full responsibility for our organization and
							this web site.
						</p>
					</Col>
					<Col xs={6}>
						<a href="http://webatberkeley.org/">
							<p
								style={{
									margin: '0',
									padding: '.7rem',
									fontSize: '18px',
									marginBottom: '2rem',
								}}
							>
								This Site was Designed and Built by Web Development at Berkeley
							</p>
						</a>
						<img
							className="aligncenter size-full"
							style={style.OCF}
							src="https://www.ocf.berkeley.edu/hosting-logos/ocf-hosted-penguin-dark.svg"
							alt="hosted by the ocf"
						></img>
					</Col>
				</Row>
			</Container>
		</footer>
	);
};

const style = {
	Logo: {
		maxWidth: '60%',
	},
	DisclaimerText: {
		padding: '1rem 0',
		fontSize: '16px',
	},
	OCF: {
		width: '125',
		height: '50',
	},
};

export default Landing;
