import React from 'react';

import SiteNavbar from './SiteNavbar';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import TeamPhotoSmall from '../img/teamPhotoSmall.png';

const About = () => {
	return (
		<div>
			<SiteNavbar />
			<Container fluid style={style.Header}>
				<Row
					className="justify-content-center align-items-center"
					noGutters="true"
				>
					<h1 style={style.Header1}>About Us</h1>
				</Row>
			</Container>
			<Container fluid>
				<Row className="justify-content-center">Founded in january 2020</Row>
				<Row className="justify-content-center">
					FEB emerged in January 2020 from Berkeley Hyperloop (bLoop) after the
					SpaceX Hyperloop Pod Competition became inactive. bLoop had previously
					competed in the 2016, 2017, 2018, and 2019 Hyperloop competitions.
					Carried through to the new team are nearly all of the former’s team’s
					members and external relations, as well as the goal of equipping
					Berkeley students with the technical acumen needed to help the world
					transition to sustainable transportation.
				</Row>
			</Container>
		</div>
	);
};

const style = {
	Header: {
		backgroundImage: `url(${TeamPhotoSmall})`,
		backgroundRepeat: 'no-repeat',
		backgroundSize: '100%',
		padding: '0',
	},
	Header1: {
		fontSize: '72px',
		fontFamily: 'Titillium Web',
		padding: '6% 0',
	},
};

export default About;
